import React, {useContext, useEffect, useState} from 'react';
import {Button, Container, Row} from "react-bootstrap";
import {DateRangePicker} from "rsuite";
import OrderList from "../components/OrderList";
import {Context} from "../index";
import {fetchOrders} from "../http/orderApi";
import BoxTopList from "../components/BoxTopList";

const ReportOrder = () => {

    const {order} = useContext(Context)

    const [show,setShow] = useState(false)

    const [dateRange,setDateRange] = useState([])
    const [filteredOrders,setFilteredOrders] = useState([])
    const [price,setPrice] = useState(0)
    const [selfPrice, setSelfPrice] = useState(0)
    const [boxAmount, setBoxAmount] = useState(0)
    const [orderAmount, setOrderAmount] = useState(0)
    const [postCardAmount, setPostCardAmount] = useState(0)
    const [box,setBox] = useState([])
    const [postCard,setPostCard] = useState([])



    const createTopPostCard = () =>{
        let postCardIds = []
        let resPostCard = []
        order.filteredoOders.forEach( order => {
            order.post_cards.forEach(postCard =>{
                if(postCardIds.includes(postCard.id)){
                    resPostCard = resPostCard.map(i => i.postCard.id === postCard.id?{...i,amount:i.amount+1}:i)
                }else {
                    postCardIds.push(postCard.id)
                    resPostCard.push({postCard:postCard, amount:1})
                }
            })

        } )
        console.log("Postids")
        console.log(postCardIds)
        console.log("resPostCard")
        resPostCard.sort((a, b) => a.amount > b.amount? -1 : 1)
        console.log(resPostCard)

    }
    const createTopBox = () =>{
        let boxIds = []
        let resBox = []
        order.filteredoOders.forEach( order => {
            order.boxes.forEach(box =>{
                if(boxIds.includes(box.id)){
                    resBox = resBox.map(i => i.box.id === box.id?{...i,amount:i.amount+box.order_box.amount}:i)
                }else {
                    boxIds.push(box.id)
                    resBox.push({box:box, amount:box.order_box.amount})
                }
            })
        } )
        console.log("ids")
        console.log(boxIds)
        console.log("resBox")
        console.log(resBox)
        resBox.sort((a, b) => a.amount > b.amount? -1 : 1)
        console.log("resBox After Sort")
        console.log(resBox)
        setBox(resBox)
    }


    const filterOrder = () => {
        console.log("filter:")
        let startDate = Date.parse(dateRange[0])/100000
        let endDate = Date.parse(dateRange[1])/100000
        order.orders.forEach( i =>{
            let checkDate = Date.parse(i.createdAt)/100000
            if(checkDate <= endDate){
                if (checkDate >= startDate)
                    order.setFilteredOrders([...order.filteredoOders,i])

            }
        })

    }

    const calcOrderAmount = () =>{
        let res = 0
        order.filteredoOders.forEach( order => res+=1)
        setOrderAmount(res)
        return res
    }

    const calcBoxAmount = () =>{
        let res = 0
        order.filteredoOders.forEach( order => res+= order.boxes.length)
        setBoxAmount(res)
        return res
    }
    const calcPostCardAmount = () =>{
        let res = 0
        order.filteredoOders.forEach( order => res+= order.post_cards.length)
        setPostCardAmount(res)
        return res
    }
    const calcPrice = () =>{
        let res = 0
        order.filteredoOders.forEach( order => res+=order.price)
        setPrice(res)
        return res
    }

    const calcSelfPrice = () =>{

    }

    const  showOrders =  () =>{
        order.setFilteredOrders([])
        filterOrder()
        console.log("filteredoOders")
        console.log(order.filteredoOders)
        console.log( 'Кількість замовлень: ' + calcOrderAmount())
        console.log( 'Зароблено : ' + calcPrice())
        console.log( 'Всього боксів : ' + calcBoxAmount())
        console.log( 'Всього відкриток : ' + calcPostCardAmount())
        createTopBox()
        createTopPostCard()
        setShow(true)


    }

    useEffect(()=>{
        fetchOrders().then(data => {
            order.setOrders(data.rows)
        })

    },[])
    return (
       <Container>
           <Row>
               <div className="d-flex justify-content-center">
                   <div>
                       <DateRangePicker
                           showOneCalendar
                           size="lg"
                           appearance="subtle"
                           placeholder="Оберіть період"
                           style={{ width: 200 }}
                           value={dateRange}
                           onChange={(e) => {
                               setDateRange(e)
                           }}
                       />
                       <Button
                           onClick={()=> showOrders()}
                       >
                        Показати
                       </Button>

                   </div>


               </div>
           </Row>
           {show
               ?<div>
                       <div>Зароблено: {price}</div>
                       <div>Всього боксів: {boxAmount}</div>
                       <div>Всього замовлень: {orderAmount}</div>
                       <div>Всього відкриток: {postCardAmount}</div>

                       <div>
                           <h2>Топ боксів</h2>
                           <BoxTopList boxes={box} />
                       </div>
                </div>:<div></div>}



       </Container>
    );
};

export default ReportOrder;
