import React from 'react';
import {Button, Card, Col, Container, Image, Row} from "react-bootstrap";

const BoxPage = () => {
    const box = {id:1,name:"Перший бокс", price:2334, cbox:'Червона', img:'https://p2.zoon.ru/preview/U3a1uyz3s8zY1zpnQ3x9vg/2400x1500x85/1/7/5/original_5d5d700467cd9302f61f1858_5d5d70990a995.jpg'}
    const composition = [
        {id:1, name:'Milka', amount:3},
        {id:2, name:'Milka', amount:34},
        {id:3, name:'Milka', amount:3},
        {id:4, name:'Oreo', amount:3},
        {id:5, name:'Milka', amount:3},
        {id:6, name:'Sen', amount:3},
        {id:7, name:'Milka', amount:3},
        {id:8, name:'Milka', amount:3}
    ]
        return (
        <Container>
            <Row className="mt-3">
                <Col md={4}>
                    <Image width={300} height={300} src={box.img}/>
                </Col>
                <Col md={4}>
                    <div className="mt-2" style={{fontSize:24}}>
                        {box.name}
                    </div>
                </Col>
                <Col md={4}>
                    <Card
                        className="d-flex flex-column align-items-center justify-content-around"
                        style={{fontSize:32, width:300, height:300, border:'5px solid lightgray'}}
                    >
                        <h3>{box.price} грн.</h3>
                        <div className="d-flex flex-column">
                            <Button variant={"outline-info"} className="m-2">Змінити</Button>
                            <Button variant={"outline-danger"} className="m-2">Видалити</Button>
                        </div>

                    </Card>

                </Col>
            </Row>
            <Row className="d-flex flex-column m-3">
                {composition.map((line, index) =>
                    <Row key={line.id} style={{background: index % 2 === 0? 'lightgray': 'transparent'}}>
                        {line.name} : {line.amount}
                    </Row>
                )}
            </Row>
        </Container>
    );
};

export default BoxPage;
