import React from 'react';
import {Button, OverlayTrigger, Tooltip} from "react-bootstrap";

const BoxSort = ({select}) => {

    const renderTooltip = (props) => (
        <Tooltip {...props}>
            Simple tooltip
        </Tooltip>
    );


    const sortByName = () =>{
        select("name")
    }
    const sortByPrice = () =>{
        select("price")
    }

    return (
        <div>
            <p className="mt-2">Сортувати:</p>
            <div className="d-flex mt-2">
                <OverlayTrigger
                    placement="bottom"
                    delay={{ show: 250, hide: 400 }}
                    overlay={renderTooltip}
                >
                    <Button
                        onClick={()=> sortByName()}
                        variant="outline-primary"
                    >
                        По Назві
                    </Button>
                </OverlayTrigger>

                <Button
                    style={{marginLeft:20}}
                    onClick={()=> sortByPrice()}
                    variant="outline-primary"
                >
                    По Ціні
                </Button>
            </div>
        </div>

    );
};

export default BoxSort;
