import React, {useContext, useEffect, useState} from 'react';
import {Form, Modal, Button, Row, Col, Dropdown, Container, ModalBody, ModalTitle, Card} from "react-bootstrap";
import {createBox, fetchBoxes, fetchOneBox, updateBox} from "../../http/boxApi";
import {useLocation, useNavigate, useParams} from "react-router-dom";
import {BOXPAGE_ROUTE} from "../../utils";
import {login} from "../../http/userApi";
import data from "bootstrap/js/src/dom/data";
import {Context} from "../../index";
import ModalHeader from "react-bootstrap/ModalHeader";
import {fetchPostCards} from "../../http/postCardApi";

const CreateOrder = ({show, onHide}) => {

    const {order} = useContext(Context)
    const {box} = useContext(Context)
    const {postCard} = useContext(Context)
    const {id} = useParams()
    //const [boxes,setBoxes] = useState([])
    const [selectedBox, setSelectedBox] = useState([])
    const [chosenBox, setChosenBox] = useState([{id:10}])
    const [chosenPostCard, setChosenPostCard] = useState([])

    const [status,setStatus] = useState(null)
    const [paymentStatus,setPaymentStatus] = useState(null)
    const [price, setPrice] = useState('')

    const [boxModalVisible, setBoxModalVisible] = useState(false)
    const [postCardModalVisible, setPostCardBoxModalVisible] = useState(false)

    const addOrder = () =>{
        console.log('CreateOrder')
    }
    const showBoxModal = () =>{
        fetchBoxes().then(data =>{
            box.setBoxes(data.rows)

            setBoxModalVisible(true)
            })
    }
    const showPostCardModal = () =>{
        fetchPostCards().then(data =>{
            postCard.setPostCards(data.rows)

            setPostCardBoxModalVisible(true)
        })
    }
    const addBox = () =>{

    }
    const addPostCard = () =>{

    }


    return (
        <Container>


        <Modal
            show={show}
            onHide={onHide}
            size="lg"
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                   Додати замовлення
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <div className="d-flex flex-column">
                    <Button
                        onClick={showBoxModal}
                        className="mt-3"
                    >
                        Додати бокс
                    </Button>
                    <Button
                        onClick={showPostCardModal}
                        className="mt-3"
                    >
                        Додати відкритку
                    </Button>
                </div>

                <Form>

                    <Form.Control
                        className="mt-3"
                        onChange={e => setPrice(Number(e.target.value))}
                        placeholder={"Введіть ціну"}
                        type="number"
                        value={price}
                    />
                    <Form.Control
                        className="mt-3"
                       // onChange={e => setPrice(e.target.value)}
                        placeholder={"Введіть ім'я клієнта"}

                        //value={price}
                    />
                    <Form.Control
                        className="mt-3"
                       // onChange={e => setPrice(e.target.value)}
                        placeholder={"Введіть телефон клієнта"}
                        type="phone"
                        //value={price}
                    />
                    <Form.Control
                        className="mt-3"
                       // onChange={e => setPrice(e.target.value)}
                        placeholder={"Введіть дату відправки"}

                        //value={price}
                    />
                    <Form.Control
                        className="mt-3"
                        //onChange={e => setPrice(e.target.value)}
                        placeholder={"Введіть місто доставки"}

                        //value={price}
                    />
                    <Form.Control
                        className="mt-3"
                       // onChange={e => setPrice(e.target.value)}
                        placeholder={"Введіть відділення  НП"}

                        //value={price}
                    />
                    <Form.Control
                        className="mt-3"
                      //  onChange={e => setPrice(e.target.value)}
                        placeholder={"Текст для відкритки"}

                        //value={price}
                    />

                    <Form.Control
                        className="mt-3"
                       // onChange={e => setPrice(e.target.value)}
                        placeholder={"Коментар"}

                        //value={price}
                    />
                    <Dropdown className="mt-3">
                        <Dropdown.Toggle>{status||"Оберіть стан замовлення"}</Dropdown.Toggle>
                        <Dropdown.Menu>
                            {order.status.map(status =>
                                <Dropdown.Item
                                    onClick={() => setStatus(status.title)}
                                    key={status.id}
                                >
                                    {status.title}
                                </Dropdown.Item>
                            )}
                        </Dropdown.Menu>

                    </Dropdown>
                    <Dropdown className="mt-3">
                        <Dropdown.Toggle>{paymentStatus || "Оберіть стан оплати"}</Dropdown.Toggle>
                        <Dropdown.Menu>
                            {order.paymentStatus.map(paymentStatus =>
                                <Dropdown.Item
                                    onClick={() => setPaymentStatus(paymentStatus.title)}
                                    key={paymentStatus.id}
                                >
                                    {paymentStatus.title}
                                </Dropdown.Item>
                            )}
                        </Dropdown.Menu>

                    </Dropdown>

                </Form>
                <hr/>

            </Modal.Body>
            <Modal.Footer>
                <Button variant={"outline-danger"} onClick={onHide}>Закрити</Button>
                <Button variant={"outline-success"} onClick={addOrder}>Додати </Button>
            </Modal.Footer>
        </Modal>

        <Modal
            show={boxModalVisible}
            onHide={()=> setBoxModalVisible(false)}
            size="lg"
            centered
        >
            <ModalHeader>
                <ModalTitle>
                    Додати бокс
                </ModalTitle>
            </ModalHeader>
            <ModalBody>
                <Row className="d-flex justify-content-around">
                    {box.boxes.map(box =>
                        <Card key={box.id} style={{ width: 300 }} className="m-2 p-0">
                            <Card.Img variant="top" src={process.env.REACT_APP_API_URL + box.img} width={300} height={300} />
                            <Card.Body>
                                <Card.Title>{box.name}</Card.Title>
                                <Card.Text>
                                    {box.price} грн
                                </Card.Text>
                                <Button
                                    onClick={()=> setChosenBox([...chosenBox,box.id])}
                                    variant="primary"
                                >
                                    Додати
                                </Button>
                            </Card.Body>
                        </Card>

                    )}
                </Row>

            </ModalBody>

        </Modal>
            <Modal
                show={postCardModalVisible}
                onHide={()=> setPostCardBoxModalVisible(false)}
                size="lg"
                centered
            >
                <ModalHeader>
                    <ModalTitle>
                        Додати відкритку
                    </ModalTitle>
                </ModalHeader>
                <ModalBody>
                    <Row className="d-flex justify-content-around">
                        {postCard.postCards.map(postCard =>
                            <Card key={postCard.id} style={{ width: 300 }} className="m-2 p-0">
                                <Card.Img variant="top" src={process.env.REACT_APP_API_URL + postCard.img} width={300} height={300} />
                                <Card.Body>
                                    <Card.Title>{postCard.name}</Card.Title>
                                    <Card.Text>
                                        {postCard.price} грн
                                    </Card.Text>
                                    <Button
                                        onClick={()=> setChosenPostCard([...chosenBox,box.id])}
                                        variant="primary"
                                    >
                                        Додати
                                    </Button>
                                </Card.Body>
                            </Card>

                        )}
                    </Row>

                </ModalBody>

            </Modal>
        </Container>
    );
};

export default CreateOrder;
