import React, {useContext, useEffect, useState} from 'react';
import {Button, Col, Container, FormControl, InputGroup, Row} from "react-bootstrap";
import SideBar from "../components/SideBar";
import BoxList from "../components/BoxList";
import CreateBox from "../components/modals/CreateBox";
import {Context} from "../index";
import {fetchBoxes} from "../http/boxApi";
import {fetchPostCards} from "../http/postCardApi";
import PostCardList from "../components/PostCardList";
import BoxSort from "../components/BoxSort";

const Main = () => {


    const [selectedSort,setSelectedSort] = useState('name')
    const [boxVisible, setBoxVisible] = useState(false)

    const {box} = useContext(Context)
    const {postCard} = useContext(Context)


    const searchBoxList = () =>{
        setSearchedBox(box.boxes.filter(box => box.name.toLowerCase().includes(searchQueryBox.toLowerCase())))
    }
    
    const selectSort =(sort) =>{
        setSelectedSort(sort)
    }

    useEffect(()=>{
        fetchBoxes().then(data => {
            box.setBoxes(data.rows)
        })
        fetchPostCards().then(data =>{
            postCard.setPostCards(data.rows)
        })
    },[])


    return (
        <Container>
            <Row>
                <Col md={3}>
                    <SideBar/>
                </Col>
                <Col md={9}>

                    <InputGroup className="mb-3">
                        <FormControl
                            placeholder="Пошук.."
                            value={searchQueryBox}
                            onChange={event => {
                                setSearchQueryBox(event.target.value)
                                searchBoxList()
                            }}

                        />

                    </InputGroup>
                    <BoxSort select ={selectSort}/>
                    <BoxList sort={selectedSort}/>
                    <PostCardList/>
                    <Button
                        className="m-auto mt-3"
                        variant={"outline-dark"}
                        style={{width:300}}
                        onClick={()=>setBoxVisible(true)}
                    >
                        Додати Бокс
                    </Button>
                    <CreateBox show={boxVisible} onHide={()=> setBoxVisible(false)}/>
                </Col>
            </Row>

        </Container>
    );
};

export default Main;
