        this._boxes = [
            {id:1,name:"Перший бокс", price:233, cbox:'Червона', img:'https://p2.zoon.ru/preview/U3a1uyz3s8zY1zpnQ3x9vg/2400x1500x85/1/7/5/original_5d5d700467cd9302f61f1858_5d5d70990a995.jpg'},
