            {id:3,name:"Третій бокс", price:304, cbox:'Біла', img:'https://p2.zoon.ru/preview/U3a1uyz3s8zY1zpnQ3x9vg/2400x1500x85/1/7/5/original_5d5d700467cd9302f61f1858_5d5d70990a995.jpg'},
            {id:4,name:"Третій бокс", price:304, cbox:'Біла', img:'https://p2.zoon.ru/preview/U3a1uyz3s8zY1zpnQ3x9vg/2400x1500x85/1/7/5/original_5d5d700467cd9302f61f1858_5d5d70990a995.jpg'},

        ]
        this._selectedType = {}
        makeAutoObservable(this)
    }

    setPostCards(postCards){
        this._postcards = postCards
    }


    setBoxes(box){
        this._boxes = box
    }

    setTypes(types){
        this._types = types
    }

    setSelectedType(type){
        this._selectedType = type
    }

    get postCards() {
        return this._postcards
    }
    get types() {
        return this._types
    }

    get boxes() {
        return this._boxes
    }
    get selectedType(){
        return this._selectedType
    }
}
