import React, {useEffect, useState} from 'react';
import {Button, Card, Col, Container, Image, Row} from "react-bootstrap";
import {useLocation, useNavigate, useParams} from 'react-router-dom'
import {deleteBox, fetchOneBox} from "../http/boxApi";
import data from "bootstrap/js/src/dom/data";
import {BOXPAGE_ROUTE, MAIN_ROUTE} from "../utils";
import CreateBox from "../components/modals/CreateBox";

const BoxPage = () => {

    const navigate = useNavigate()
    const [box,setBox] = useState({composition:[]})
    const {id} = useParams()
    const [boxVisible, setBoxVisible] = useState(false)


    const removeBox = () =>{
        try {
            deleteBox(id).then(data => navigate(MAIN_ROUTE) )
        }catch (e) {
            console.log('Помилка при видалені')}
    }

    const updBox = () => {
        try {
            setBoxVisible(true)

        }catch (e) {
            console.log('Помилка при оновлені')}
    }
    useEffect(()=>{
        fetchOneBox(id).then(data=> {
            setBox(data)
        })
    },[])
        return (
        <Container>

                    <Image width={300} height={300} src={'http://localhost:5000/'+box.img}/>


                    <div className="mt-2" style={{fontSize:24}}>
                        {box.name}
                    </div>

                    <Card
                        className="d-flex flex-column align-items-center justify-content-around"
                        style={{fontSize:32, width:300, height:300, border:'5px solid lightgray'}}
                    >
                        <h3>{box.price} грн.</h3>
                        <div className="d-flex flex-column">
                            <Button
                                onClick={updBox}
                                variant={"outline-info"}
                                className="m-2">Змінити</Button>
                            <Button
                                onClick={removeBox}
                                variant={"outline-danger"}
                                className="m-2"
                            >
                                Видалити
                            </Button>
                        </div>

                    </Card>


         

                <h2 className="m-2">Склад</h2>
                {box.composition.map((line, index) =>
                    <div className="p-1" key={line.id} style={{background: index % 2 === 0? 'lightgray': 'transparent'}}>
                        {line.name} : {line.amount}
                    </div>
                )}

            <CreateBox show={boxVisible} onHide={()=> setBoxVisible(false)} oldComposition={box.composition}/>
        </Container>
    );
};

export default BoxPage;
