import React, {useContext, useEffect, useState} from 'react';
import {Form, Modal, Button, Row, Col, Dropdown, Container, ModalBody, ModalTitle} from "react-bootstrap";
import {createBox, fetchBoxes, fetchOneBox, updateBox} from "../../http/boxApi";
import {useLocation, useNavigate, useParams} from "react-router-dom";
import {BOXPAGE_ROUTE} from "../../utils";
import {login} from "../../http/userApi";
import data from "bootstrap/js/src/dom/data";
import {Context} from "../../index";
import ModalHeader from "react-bootstrap/ModalHeader";

const CreateOrder = ({show, onHide}) => {

    const {order} = useContext(Context)
    const {box} = useContext(Context)
    const {id} = useParams()
    //const [boxes,setBoxes] = useState([])
    const [selectedBox, setSelectedBox] = useState([])
    const [chosenBox, setChosenBox] = useState([{id:10}])
    const [chosenPostCard, setChosenPostCard] = useState([])

    const [status,setStatus] = useState(null)
    const [paymentStatus,setPaymentStatus] = useState(null)
    const [price, setPrice] = useState('')

    const [boxModalVisible, setBoxModalVisible] = useState(false)
    const [postCardModalVisible, setPostCardBoxModalVisible] = useState(false)

    const addOrder = () =>{
        console.log('CreateOrder')
    }
    const showBoxModal = () =>{
        fetchBoxes().then(data =>{
            box.setBoxes(data.rows)
            setBoxModalVisible(true)
            console.log(box.boxes)
            }
        )



    }
    const addBox = () =>{

    }
    const addPostCard = () =>{

    }


    return (
        <Container>


        <Modal
            show={show}
            onHide={onHide}
            size="lg"
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                   Додати замовлення
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <div className="d-flex flex-column">
                    <Button
                        onClick={showBoxModal}
                        className="mt-3"
                    >
                        Додати бокс
                    </Button>
                    <Button className="mt-3">
                        Додати відкритку
                    </Button>
                </div>

                <Form>

                    <Form.Control
                        className="mt-3"
                        onChange={e => setPrice(Number(e.target.value))}
                        placeholder={"Введіть ціну"}
                        type="number"
                        value={price}
                    />
                    <Form.Control
                        className="mt-3"
                       // onChange={e => setPrice(e.target.value)}
                        placeholder={"Введіть ім'я клієнта"}

                        //value={price}
                    />
                    <Form.Control
                        className="mt-3"
                       // onChange={e => setPrice(e.target.value)}
                        placeholder={"Введіть телефон клієнта"}
                        type="phone"
                        //value={price}
                    />
                    <Form.Control
                        className="mt-3"
                       // onChange={e => setPrice(e.target.value)}
                        placeholder={"Введіть дату відправки"}

                        //value={price}
                    />
                    <Form.Control
                        className="mt-3"
                        //onChange={e => setPrice(e.target.value)}
                        placeholder={"Введіть місто доставки"}

                        //value={price}
                    />
                    <Form.Control
                        className="mt-3"
                       // onChange={e => setPrice(e.target.value)}
                        placeholder={"Введіть відділення  НП"}

                        //value={price}
                    />
                    <Form.Control
                        className="mt-3"
                      //  onChange={e => setPrice(e.target.value)}
                        placeholder={"Текст для відкритки"}

                        //value={price}
                    />

                    <Form.Control
                        className="mt-3"
                       // onChange={e => setPrice(e.target.value)}
                        placeholder={"Коментар"}

                        //value={price}
                    />
                    <Dropdown className="mt-3">
                        <Dropdown.Toggle>{status||"Оберіть стан замовлення"}</Dropdown.Toggle>
                        <Dropdown.Menu>
                            {order.status.map(status =>
                                <Dropdown.Item
                                    onClick={() => setStatus(status.title)}
                                    key={status.id}
                                >
                                    {status.title}
                                </Dropdown.Item>
                            )}
                        </Dropdown.Menu>

                    </Dropdown>
                    <Dropdown className="mt-3">
                        <Dropdown.Toggle>{paymentStatus || "Оберіть стан оплати"}</Dropdown.Toggle>
                        <Dropdown.Menu>
                            {order.paymentStatus.map(paymentStatus =>
                                <Dropdown.Item
                                    onClick={() => setPaymentStatus(paymentStatus.title)}
                                    key={paymentStatus.id}
                                >
                                    {paymentStatus.title}
                                </Dropdown.Item>
                            )}
                        </Dropdown.Menu>

                    </Dropdown>

                </Form>
                <hr/>

            </Modal.Body>
            <Modal.Footer>
                <Button variant={"outline-danger"} onClick={onHide}>Закрити</Button>
                <Button variant={"outline-success"} onClick={addOrder}>Додати </Button>
            </Modal.Footer>
        </Modal>

        <Modal
            show={boxModalVisible}
            onHide={()=> setBoxModalVisible(false)}
            size="lg"
            centered
        >
            <ModalHeader>
                <ModalTitle>
                    Додати бокс
                </ModalTitle>
            </ModalHeader>
            <ModalBody>
                {box.boxes.map(box =>
                <Row key={box.id}>
                    
                    {box.name}
                </Row>
                )}
            </ModalBody>

        </Modal>
        </Container>
    );
};

export default CreateOrder;
