import React, {useContext, useEffect, useState} from 'react';
import {Form, Modal, Button, Row, Col, Dropdown} from "react-bootstrap";
import {createBox, fetchOneBox, updateBox} from "../../http/boxApi";
import {useLocation, useNavigate, useParams} from "react-router-dom";
import {BOXPAGE_ROUTE} from "../../utils";
import {login} from "../../http/userApi";
import data from "bootstrap/js/src/dom/data";
import {Context} from "../../index";

const CreateOrder = ({show, onHide}) => {

    const {order} = useContext(Context)
    const {id} = useParams()
    const location = useLocation()
    const [chosenBox, setChosenBox] = useState([])
    const [chosenPostCard, setChosenPostCard] = useState([])
    const [status,setStatus] = useState(null)
    const [paymentStatus,setPaymentStatus] = useState(null)
    const isUpd = location.pathname.includes(BOXPAGE_ROUTE);
    const [deletedComposition, setDeletedComposition] = useState([])
    const [composition, setComposition]= useState([])
    const [name, setName] = useState('')
    const [price, setPrice] = useState('')
    const [file, setFile] = useState(null)





    return (
        <Modal
            show={show}
            onHide={onHide}
            size="lg"
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                   Додати замовлення
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <div className="d-flex flex-column">
                    <Button className="mt-3">
                        Додати бокс
                    </Button>
                    <Button className="mt-3">
                        Додати відкритку
                    </Button>
                </div>

                <Form>

                    <Form.Control
                        className="mt-3"
                        onChange={e => setPrice(Number(e.target.value))}
                        placeholder={"Введіть ціну"}
                        type="number"
                        value={price}
                    />
                    <Form.Control
                        className="mt-3"
                       // onChange={e => setPrice(e.target.value)}
                        placeholder={"Введіть ім'я клієнта"}

                        //value={price}
                    />
                    <Form.Control
                        className="mt-3"
                       // onChange={e => setPrice(e.target.value)}
                        placeholder={"Введіть телефон клієнта"}
                        type="phone"
                        //value={price}
                    />
                    <Form.Control
                        className="mt-3"
                       // onChange={e => setPrice(e.target.value)}
                        placeholder={"Введіть дату відправки"}

                        //value={price}
                    />
                    <Form.Control
                        className="mt-3"
                        //onChange={e => setPrice(e.target.value)}
                        placeholder={"Введіть місто доставки"}

                        //value={price}
                    />
                    <Form.Control
                        className="mt-3"
                       // onChange={e => setPrice(e.target.value)}
                        placeholder={"Введіть відділення  НП"}

                        //value={price}
                    />
                    <Form.Control
                        className="mt-3"
                      //  onChange={e => setPrice(e.target.value)}
                        placeholder={"Текст для відкритки"}

                        //value={price}
                    />

                    <Form.Control
                        className="mt-3"
                       // onChange={e => setPrice(e.target.value)}
                        placeholder={"Коментар"}

                        //value={price}
                    />
                    <Dropdown className="mt-3">
                        <Dropdown.Toggle>{status||"Оберіть стан замовлення"}</Dropdown.Toggle>
                        <Dropdown.Menu>
                            {order.status.map(status =>
                                <Dropdown.Item
                                    onClick={() => setStatus(status.title)}
                                    key={status.id}
                                >
                                    {status.title}
                                </Dropdown.Item>
                            )}
                        </Dropdown.Menu>

                    </Dropdown>
                    <Dropdown className="mt-3">
                        <Dropdown.Toggle>{paymentStatus || "Оберіть стан оплати"}</Dropdown.Toggle>
                        <Dropdown.Menu>
                            {order.paymentStatus.map(paymentStatus =>
                                <Dropdown.Item
                                    onClick={() => setPaymentStatus(paymentStatus.title)}
                                    key={paymentStatus.id}
                                >
                                    {paymentStatus.title}
                                </Dropdown.Item>
                            )}
                        </Dropdown.Menu>

                    </Dropdown>

                </Form>
                <hr/>

            </Modal.Body>
            <Modal.Footer>
                <Button variant={"outline-danger"} onClick={onHide}>Закрити</Button>
                <Button variant={"outline-success"} onClick={addBox}>{isUpd?'Зберегти':'Додати '}</Button>
            </Modal.Footer>
        </Modal>
    );
};

export default CreateOrder;
