import React, {useContext, useEffect, useState} from 'react';
import {Button, Col, Container, Row} from "react-bootstrap";
import {Context} from "../index";
import SideBar from "../components/SideBar";
import CreateBox from "../components/modals/CreateBox";
import OrderList from "../components/OrderList";
import CreateOrder from "../components/modals/CreateOrder";
import {fetchBoxes} from "../http/boxApi";
import {fetchPostCards} from "../http/postCardApi";
import {fetchOneOrder,fetchOrders} from "../http/orderApi";
import OrderFilter from "../components/OrderFilter";
import OrderReport from "../components/modals/OrderReport";


const Order = () => {
    const [boxVisible, setBoxVisible] = useState(false)
    const {order} = useContext(Context)
    const [dateRange,setDateRange] = useState([])

    const [filteredOrders,setFilteredOrders] = useState([])


    const removeFilter = () =>{
        setFilteredOrders([])
    }
    const filterOrder = () => {

        let startDate = Date.parse(dateRange[0])/100000
        let endDate = Date.parse(dateRange[1])/100000
        order.orders.forEach( i =>{
            let checkDate = Date.parse(i.createdAt)/100000
            if(checkDate <= endDate){
                if (checkDate >= startDate)
                    setFilteredOrders((filtered) => {
                    return [
                        ...filtered,
                        i
                    ]
                })

            }
        })
        console.log(filteredOrders)
    }



    const addDateRange = (date) =>{
            setDateRange(date)

    }

    useEffect(()=>{
        fetchOrders().then(data => {
            order.setOrders(data.rows)
        })

    },[])
    return (
        <Container>
            <Row>
                <Col md={3}>
                    <OrderFilter addDateRange={addDateRange} dateRange={dateRange}/>
                    <Button
                        onClick={()=>filterOrder()}
                    >
                        Застосувати
                    </Button>
                    <Button
                        onClick={()=>removeFilter()}
                    >
                        Скинути
                    </Button>

                </Col>
                <Col md={7}>
                    {console.log("filteredOrders")}
                    {console.log(filteredOrders)}
                    <OrderList filteredOrders ={filteredOrders} />
                </Col>
                <Col md={1}>
                    <Button
                        className="m-auto mt-3"
                        variant={"outline-dark"}
                        style={{width:200}}
                        onClick={()=>setBoxVisible(true)}
                    >
                        Додати Замовлення
                    </Button>
                </Col>
            </Row>
            <CreateOrder
                show={boxVisible}
                onHide={()=> setBoxVisible(false) }
            />

        </Container>

    );
};

export default Order;
