import React, {useEffect, useState} from 'react';
import {fetchOneBox} from "../http/boxApi";
import {fetchOneOrder} from "../http/orderApi";
import {useParams} from "react-router-dom";
import {Button, Card, Container, Row} from "react-bootstrap";

const OrderPage = () => {

    const [order,setOrder] = useState({})
    const {id} = useParams()

    function toISODate(milliseconds) {
        var date = new Date(milliseconds);
        var y = date.getFullYear()
        var m = date.getMonth() + 1;
        var d = date.getDate();
        m = (m < 10) ? '0' + m : m;
        d = (d < 10) ? '0' + d : d;
        return [d, m, y].join('.');
    }


    useEffect(()=>{
        fetchOneOrder(id).then(data=> {
            setOrder(data)
            console.log("Userefect")
            console.log(data)
            console.log(order.id)
        })
    },[])

    let date = toISODate(Date.parse(order.createdAt))
    return (
        <Container>
            <Row>
                <div>
                    <h3>Замовлення №{order.id}</h3>
                </div>
                <div>Клієнт - {order.client_name}</div>
                <div>Місто - {order.city}, відділення - {order.department}</div>
                <div>Телефон - {order.client_phone}</div>
                <div> До: {order.date_to_send || 'Не визначено'}</div>
                <div>Дата замовлення {date}</div>
                <div>Ціна {order.price}</div>
                <Row>
                    <h1>Бокси:</h1>
                    {order.boxes
                        ?
                        order.boxes.map(box =>
                            <Card key={box.id} style={{ width: 300 }} className="m-2 p-0">
                                <Card.Img variant="top" src={process.env.REACT_APP_API_URL + box.img} width={300} height={300} />
                                <Card.Body>
                                    <Card.Title>{box.name}</Card.Title>
                                    <Card.Text>
                                        <div className="d-flex justify-content-between p-1">
                                            <div>
                                                {box.price} грн

                                            </div>
                                            <div>
                                               Кількість: {box.order_box.amount}
                                            </div>
                                        </div>

                                    </Card.Text>

                                </Card.Body>
                            </Card>
                        )
                    :<h1>Боксів не знайдено(</h1>}
                </Row>
                <Row>
                    <h1>Бокси:</h1>
                    {order.post_сards
                        ?'K>ree'

                        :<h1>Відкриток не знайдено(</h1>}
                </Row>
            </Row>
        </Container>
    );
};

export default OrderPage;
