import React, {useEffect, useState} from 'react';
import {deleteBox, fetchOneBox} from "../http/boxApi";
import {fetchOneOrder,deleteOrder} from "../http/orderApi";
import {useNavigate, useParams} from "react-router-dom";
import {Button, Card, Container, Row} from "react-bootstrap";
import {BOXPAGE_ROUTE, MAIN_ROUTE, ORDER_ROUTE, POSTCARD_ROUTE} from "../utils";
import CreateOrder from "../components/modals/CreateOrder";
import '../style/pages/OrderPage.css'
const OrderPage = () => {

    const [boxVisible, setBoxVisible] = useState(false)
    const [order,setOrder] = useState([])
    const [postCards,setPostCards] = useState([])
    const [box,setBox] = useState([])
    const {id} = useParams()
    const navigate = useNavigate()



    const removeOrder = () =>{
        try {
            deleteOrder(id).then(data => navigate(ORDER_ROUTE) )
        }catch (e) {
            console.log('Помилка при видалені')}
    }

    const updOrder = () => {
        try {
            setBoxVisible(true)

        }catch (e) {
            console.log('Помилка при оновлені')}
    }

    function toISODate(milliseconds) {
        var date = new Date(milliseconds);
        var y = date.getFullYear()
        var m = date.getMonth() + 1;
        var d = date.getDate();
        m = (m < 10) ? '0' + m : m;
        d = (d < 10) ? '0' + d : d;
        return [d, m, y].join('.');
    }


    useEffect(()=>{
        fetchOneOrder(id).then(data=> {
            setOrder(data)
            setPostCards(data.post_cards)
            setBox(data.boxes)
        })
    },[])

    let date = toISODate(Date.parse(order.createdAt))
    return (
        <div>


        <div className=" container d-flex ">


        <div className="order-info">
            <div className="orderinfo-item">
                <div className="orderinfo-item1">
                    ПІБ
                </div>
                <div className="orderinfo-item2">
                    {order.client_name}
                </div>
            </div>
            <div className="orderinfo-item">
                <div className="orderinfo-item1">
                    МІСТО
                </div>
                <div className="orderinfo-item2">
                    {order.city}
                </div>
            </div>
            <div className="orderinfo-item">
                <div className="orderinfo-item1">
                    ТЕЛЕФОН
                </div>
                <div className="orderinfo-item2">
                    {order.client_phone}
                </div>
            </div>
            <div className="orderinfo-item">
                <div className="orderinfo-item1">
                    ПІБ
                </div>
                <div className="orderinfo-item2">
                    {order.client_name}
                </div>
            </div>
            <div className="orderinfo-item">
                <div className="orderinfo-item1">
                    ПІБ
                </div>
                <div className="orderinfo-item2">
                    {order.client_name}
                </div>
            </div>
            <div>Місто - {order.city}, відділення - {order.department}</div>
            <div>Телефон - {order.client_phone}</div>
            <div> До: {order.date_to_send || 'Не визначено'}</div>
            <div>Дата замовлення {date}</div>
            <div>Ціна {order.price}</div>
            <div className="order-btns">
                <Button
                    onClick={()=>setBoxVisible(true)}
                    variant="outline-primary"
                >
                    Змінити
                </Button>
                <Button
                    onClick={()=> removeOrder()}
                    variant="outline-danger"
                >
                    Видалити
                </Button>
            </div>
        </div>
        <div className="content">
            <h4 >Бокси:</h4>
            <div className="card-list">
                {box.length !==0
                    ?
                    order.boxes.map(box =>
                        <div  className="card-list-item" >
                            <div
                                onClick={() => navigate(BOXPAGE_ROUTE+'/' + box.id)}
                            >


                                <img  width={210} height={210} src={process.env.REACT_APP_API_URL + box.img}/>
                                <div className="text-card">
                                    <div className="name-card">{box.name}</div>
                                    <div className="price-card">{box.order_box.amount} шт.</div>
                                </div>

                            </div>
                        </div>
                    )
                    :<h1>Боксів не знайдено(</h1>}
            </div>

            <h1>Декори:</h1>
            {postCards.length !== 0
                ?postCards.map(postCard =>
                    <Card
                        onClick={()=> navigate(POSTCARD_ROUTE + '/' + postCard.id)}
                        key={postCard.id}
                        style={{ width: 300, cursor:"pointer"}}
                        className="m-2 p-0"
                    >
                        <Card.Img variant="top" src={process.env.REACT_APP_API_URL + postCard.img} width={300} height={300} />
                        <Card.Body>
                            <Card.Title>{postCard.name}</Card.Title>
                            <Card.Text>
                                <div className="d-flex justify-content-between p-1">
                                    <div>
                                        {postCard.price} грн
                                    </div>
                                    <div>
                                        Кількість: {postCard.order_post_card.amount}
                                    </div>
                                </div>
                            </Card.Text>
                        </Card.Body>
                    </Card>)
                :<h1>Декорів не знайдено(</h1>}

        </div>


        </div>
            <CreateOrder
                show={boxVisible}
                onHide={()=> setBoxVisible(false)}
                curOrder={order}
                isUpd={true}

            />

        </div>
    );
};

export default OrderPage;
