import React, {useContext, useEffect, useState} from 'react';
import {observer} from "mobx-react-lite";
import {Context} from "../index";
import {Row} from "react-bootstrap";
import BoxItem from "./BoxItem";

const BoxList = observer( ({sort}) => {
    const {box} = useContext(Context)
    const [sortedBoxes,setSortedBoxes] = useState([])

    useEffect(()=>{
        setSortedBoxes(box.boxes)
        sortBox()
    },[])

    const sortBox = () =>{
        let resSort = []
        switch (sort) {
            case 'price':
                console.log('price')
                resSort =  setSortedBoxes.sort((a, b) => a.name > b.name ? 1 : -1);
                console.log(resSort)
                break
            default:
                console.log('name')
                resSort =  setSortedBoxes.sort((a, b) => a.name > b.name ? 1 : -1);
                console.log(resSort)
                break
        }
    }


    return (
            <Row className="d-flex mt-5" >
                {box.boxes.map(box =>
                    <BoxItem key={box.id} box={box}/>
                )}

            </Row>

    );
});

export default BoxList;
