import React, {useContext, useState} from 'react';
import {Form, Modal, Button, Row, Col} from "react-bootstrap";
import {Context} from "../../index";

const CreateBox = ({show, onHide}) => {
    const {box} = useContext(Context)
    const [composition, setComposition]= useState([{title:'', amount:'', number: Date.now()}])
    const [name, setName] = useState('')
    const [price, setPrice] = useState(0)
    const [file, setFile] = useState(null)
    const addComposition = () => {
         setComposition([...composition,{title:'', amount:'', number: Date.now()}])
    }
    const removeComposition = (number) => {
        setComposition(composition.filter(i => i.number !== number))
    }
    const changeCoposition = (key,value,number) =>{
        setComposition(composition.map(i => i.number === number ? {...i, [key]: value} : i))
    }
    const selectFile= (e) =>{
       setFile(e.target.files[0])

    }

    const addBox = () => {
        const formData =  
        createBox(box)
    }
    return (
        <Modal
            show={show}
            onHide={onHide}
            size="lg"
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    Додати Бокс
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Form.Control
                        className="mt-3"
                        onChange={e => setName(e.target.value)}
                        placeholder={"Введіть назву"}
                        value={name}
                    />
                    <Form.Control
                        className="mt-3"
                        onChange={e => setPrice(Number(e.target.value))}
                        placeholder={"Введіть ціну"}
                        type="number"
                        value={price}
                    />

                    <Form.Control
                        onChange={selectFile}
                        className="mt-3"
                        type="file"
                    />

                </Form>
                <hr/>
                <h6>Склад</h6>
                {composition.map((i) =>
                    <Row className="mt-4" key={i.number}>
                        <Col md={4}>
                            <Form.Control
                                value={i.title}
                                onChange={(e) => changeCoposition('title',e.target.value,i.number)}
                                placeholder="Введіть назву"/>
                        </Col>
                        <Col md={4}>
                            <Form.Control
                                value={i.amount}
                                onChange={(e) => changeCoposition('amount',e.target.value,i.number)}
                                placeholder="Введіть кількість" type = "number"/>
                        </Col>
                        <Col md={4}>
                           <Button
                               onClick={() => removeComposition(i.number)}
                               variant={"outline-danger"}>
                               Видалити
                           </Button>
                        </Col>
                    </Row>
                )}

                <Button
                    className="mt-3"
                    variant={"outline-dark"}
                    onClick={addComposition}
                >
                    Додати
                </Button>
            </Modal.Body>
            <Modal.Footer>
                <Button variant={"outline-danger"} onClick={onHide}>Закрити</Button>
                <Button variant={"outline-success"} onClick={addBox}>Додати</Button>
            </Modal.Footer>
        </Modal>
    );
};

export default CreateBox;
