import React, {useContext, useState} from 'react';
import {Form, Modal, Button, Row, Col} from "react-bootstrap";
import {Context} from "../../index";

const CreateBox = ({show, onHide}) => {
    const {box} = useContext(Context)
    const [composition, setComposition]= useState([])

    const addComposition = () => {
         setComposition([...composition,{title:'', amount:'', number: Date.now()}])
    }
    return (
        <Modal
            show={show}
            onHide={onHide}
            size="lg"
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    Додати Бокс
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Form.Control
                        className="mt-3"
                        placeholder={"Введіть назву"}
                    />
                    <Form.Control
                        className="mt-3"
                        placeholder={"Введіть ціну"}
                        type="number"
                    />

                    <Form.Control
                        className="mt-3"
                        type="file"
                    />

                </Form>
                <hr/>
                <Button
                    variant={"outline-dark"}
                    onClick={addComposition}
                >
                    Додати
                </Button>
                {composition.map((i) =>
                    <Row className="m-4">
                        <Col md={4}>
                            <Form.Control placeholder="Введіть назву"/>
                        </Col>
                        <Col md={4}>
                            <Form.Control placeholder="Введіть кількість" type = "number"/>
                        </Col>
                        <Col md={4}>
                           <Button variant={"outline-danger"}>
                               Видалити
                           </Button>
                        </Col>
                    </Row>
                )}
            </Modal.Body>
            <Modal.Footer>
                <Button variant={"outline-danger"} onClick={onHide}>Закрити</Button>
                <Button variant={"outline-success"} onClick={onHide}>Додати</Button>
            </Modal.Footer>
        </Modal>
    );
};

export default CreateBox;
