import React, {useContext, useEffect, useState} from 'react';
import {Badge, ListGroup, Button} from "react-bootstrap";
import {Context} from "../index";

const OrderItem = ({orderCur}) => {

    const {order} = useContext(Context)

    const [curStatus, setCurStatus] = useState('')
    const [curPaymentStatus, setCurPaymentStatus] = useState('')

    const checkStatus = (status)=>{
        let res = ''
        switch (status) {
            case "Не зібрано":
                res = 'Зібрати';
                break;
            case "Зібрано":
                res = 'Відправити';
                break;
            case "Відправлено":
                res = '';
                break;

        }
        return res

    }
    const checkPaymentStatus = ()=>{}
    useEffect(()=> {

        setCurStatus(orderCur.status)
        setCurPaymentStatus(orderCur.payment_status)

    },[])



    return (
        <div className="p-2 m-1 d-flex justify-content-between">

                <ListGroup >
                    <ListGroup.Item className="d-flex justify-content-between" style={{width:700, }}  variant="info">
                        <div>
                            <div style={{fontSize:20}}>
                                {orderCur.client_name}

                            </div>
                            <div style={{color:"grey"}}>
                                {orderCur.city}

                            </div>


                        </div>
                        <div>
                            <div>
                                <div>
                                    {orderCur.status}
                                </div>
                                <div>
                                    <Button
                                        onClick={()=> console.log(checkStatus())}
                                    >
                                        {curStatus}

                                    </Button>
                                </div>

                            </div>
                            <div style={{minWidth:190}}>
                                {orderCur.payment_status}
                            </div>

                        </div>
                    </ListGroup.Item>

                </ListGroup>

        </div>
    );
};

export default OrderItem;
