import React, {useContext, useEffect, useState} from 'react';
import {Badge, ListGroup, Button} from "react-bootstrap";
import {Context} from "../index";

const OrderItem = ({orderCur}) => {





    useEffect(()=> {

        setCurStatus(orderCur.status)
        setCurPaymentStatus(orderCur.payment_status)

    },[])



    return (
        <div className="p-2 m-1 d-flex justify-content-between">

                <ListGroup >
                    <ListGroup.Item className="d-flex justify-content-between" style={{width:700, }}  variant="info">
                        <div className="d-flex flex-column justify-content-between">
                            <div style={{fontSize:20}}>
                                {orderCur.client_name}

                            </div>
                            <div style={{color:"grey"}}>
                                {orderCur.city}

                            </div>

                        </div>
                        <div>
                            <div>
                                <div>
                                    {orderCur.status}
                                </div>
                                <div>

                                </div>

                            </div>
                            <div style={{minWidth:190}}>
                                <div>

                                    {orderCur.paymentStatus}
                                </div>
                                <div>

                                </div>


                            </div>

                        </div>
                    </ListGroup.Item>

                </ListGroup>

        </div>
    );
};

export default OrderItem;
