import React, {useContext, useEffect, useState} from 'react';
import {observer} from "mobx-react-lite";
import {Context} from "../index";
import {Col, FormControl, InputGroup, Row} from "react-bootstrap";
import BoxItem from "./BoxItem";

const BoxList = observer( ({sort}) => {
    const {box} = useContext(Context)




    const [searchQuery, setSearchQuery] = useState('')
    const [searchedBox, setSearchedBox] = useState([])

    useEffect(()=>{
        setSearchedBox(sortBox())

    },[sort])

    const searchBoxList = () =>{
        setSearchedBox(box.boxes.filter(box => box.name.toLowerCase().includes(searchQuery.toLowerCase())))
    }

    const sortBox = () =>{
        let resSort = []
        console.log('sortby', sort)
        if(searchQuery){
            resSort = searchedBox.sort((a, b) => a[sort] > b[sort] ? -1 : 1)
            console.log('sorting... with search');
            console.log(searchedBox)
            return resSort

        }else {
            box.boxes.sort((a, b) => a[sort] > b[sort] ? -1 : 1)
            console.log('sorting... without search');

        }

    }


    return (
            <Row className="d-flex mt-5" >
                <InputGroup className="mb-3">
                    <FormControl
                        placeholder="Пошук.."
                        value={searchQuery}
                        onChange={event => {
                            setSearchQuery(event.target.value)
                            searchBoxList()
                        }}

                    />

                </InputGroup>
                {searchQuery?searchedBox.map(box =>
                    <BoxItem key={box.id} box={box}/>
                ):box.boxes.map(box =>
                    <BoxItem key={box.id} box={box}/>
                    )
                }

            </Row>

    );
});

export default BoxList;
