import React from 'react';
import {Button, Col, Form, Modal, Row} from "react-bootstrap";
import {BOXPAGE_ROUTE} from "../../utils";

const CreatePostCard = ({show,onHide}) => {

    const isUpd = location.pathname.includes(BOXPAGE_ROUTE);

    return (

        <Modal
            show={show}
            onHide={onHide}
            size="lg"
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    {isUpd?'Змінити Бокс':'Додати Бокс'}
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Form.Control
                        className="mt-3"
                        onChange={e => setName(e.target.value)}
                        placeholder={"Введіть назву"}
                        value={name}
                    />
                    <Form.Control
                        className="mt-3"
                        onChange={e => setPrice(Number(e.target.value))}
                        placeholder={"Введіть ціну"}
                        type="number"
                        value={price}
                    />

                    <Form.Control
                        onChange={selectFile}
                        className="mt-3"
                        type="file"
                    />

                </Form>
                <hr/>
                <h6>Склад</h6>
                <Button
                    onClick={showOldComposition}
                >
                    Змінити склад
                </Button>
                {
                    isUpd?renderComposition.map((i)=>
                        <Row className="mt-3" key={i.id} >
                            <Col md={4} >
                                <Form.Label>
                                    <div className="m-2">
                                        {i.name}
                                    </div>

                                </Form.Label>
                            </Col>
                            <Col md={4}>
                                <Form.Label>
                                    {i.amount}
                                </Form.Label>
                            </Col>
                            <Col md={4}>
                                <Button
                                    onClick={() => removeOldComposition(i.id)}
                                    variant={"outline-danger"}>
                                    Видалити
                                </Button>
                            </Col>


                        </Row>
                    ):<Row>

                    </Row>
                }
                {composition.map((i) =>
                    <Row className="mt-4" key={i.number}>
                        <Col md={4}>
                            <Form.Control
                                value={i.title}
                                onChange={(e) => changeCoposition('name',e.target.value,i.number)}
                                placeholder="Введіть назву"/>
                        </Col>
                        <Col md={4}>
                            <Form.Control
                                value={i.amount}
                                onChange={(e) => changeCoposition('amount',e.target.value,i.number)}
                                placeholder="Введіть кількість" type = "number"/>
                        </Col>
                        <Col md={4}>
                            <Button
                                onClick={() => removeComposition(i.number)}
                                variant={"outline-danger"}>
                                Видалити
                            </Button>
                        </Col>
                    </Row>
                )}

                <Button
                    className="mt-3"
                    variant={"outline-dark"}
                    onClick={addComposition}
                >
                    Додати
                </Button>
            </Modal.Body>
            <Modal.Footer>
                <Button variant={"outline-danger"} onClick={onHide}>Закрити</Button>
                <Button variant={"outline-success"} onClick={addBox}>{isUpd?'Зберегти':'Додати '}</Button>
            </Modal.Footer>
        </Modal>
    );
};

export default CreatePostCard;
