import React from 'react';
import {Button} from "react-bootstrap";

const BoxSort = ({select}) => {

    const sortByName = () =>{
        select("name")
    }
    const sortByPrice = () =>{
        select("price")
    }

    return (
        <div>
            <p className="mt-2">Сортувати:</p>
            <div className="d-flex mt-2">
                <Button
                    onClick={()=> sortByName()}
                    variant="outline-primary"
                >
                    По Назві
                </Button>
                <Button
                    style={{marginLeft:20}}
                    onClick={()=> sortByPrice()}
                    variant="outline-primary"
                >
                    По Ціні
                </Button>
            </div>
        </div>

    );
};

export default BoxSort;
