import React, {useEffect, useState} from 'react';
import {Form, Modal, Button, Row, Col} from "react-bootstrap";
import {createBox, fetchOneBox, updateBox} from "../../http/boxApi";
import {useLocation, useNavigate, useParams} from "react-router-dom";
import {BOXPAGE_ROUTE} from "../../utils";
import {login} from "../../http/userApi";
import data from "bootstrap/js/src/dom/data";

const CreateBox = ({show, onHide}) => {

    const stop = []
    const navigate = useNavigate()
    const {id} = useParams()
    const location = useLocation()
    const isUpd = location.pathname.includes(BOXPAGE_ROUTE);
    const [oldComposition, setOldComposition] = useState([{name:'1', amount:''}])
    const [composition, setComposition]= useState([])
    const [name, setName] = useState('')
    const [price, setPrice] = useState(0)
    const [file, setFile] = useState(null)
    const addComposition = () => {
         setComposition([...composition,{name:'', amount:'', number: Date.now()}])
    }
    const removeComposition = (number) => {
        setComposition(composition.filter(i => i.number !== number))
    }
    const changeCoposition = (key,value,number) =>{
        setComposition(composition.map(i => i.number === number ? {...i, [key]: value} : i))
    }
    const selectFile= (e) =>{
       setFile(e.target.files[0])

    }
    useEffect(()=>{
        fetchOneBox(id).then(data=> {
            data.composition.map(comp =>{
                console.log(comp)
                setOldComposition([...oldComposition, comp])

            })
            console.log(oldComposition)

        })
    },stop)
    const addBox = () => {
        const formData =  new FormData()
        formData.append('name', name)
        formData.append('price', `${price}`)
        if (file) formData.append('img', file)
        if (composition) formData.append('composition', JSON.stringify(composition))
        if (isUpd){
            console.log('Змінення')
           // updateBox(id,formData).then(data=> {onHide()})
            //console.log(formData.get('img'))

        } else {
            console.log('Створення')

            //createBox(formData).then(data => onHide())
        }



    }
    return (
        <Modal
            show={show}
            onHide={onHide}
            size="lg"
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    {isUpd?'Змінити Бокс':'Додати Бокс'}
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Form.Control
                        className="mt-3"
                        onChange={e => setName(e.target.value)}
                        placeholder={"Введіть назву"}
                        value={name}
                    />
                    <Form.Control
                        className="mt-3"
                        onChange={e => setPrice(Number(e.target.value))}
                        placeholder={"Введіть ціну"}
                        type="number"
                        value={price}
                    />

                    <Form.Control
                        onChange={selectFile}
                        className="mt-3"
                        type="file"
                    />

                </Form>
                <hr/>
                <h6>Склад</h6>
                {oldComposition.map((i)=>
                    <Row className="mt-4" key={i.id} >
                        <Col md={6}>
                            <Form.Label>
                                {i.name}
                            </Form.Label>
                        </Col>
                        <Col md={6}>
                            <Form.Label>
                                {i.amount}
                            </Form.Label>
                        </Col>

                    </Row>
                )}
                {composition.map((i) =>
                    <Row className="mt-4" key={i.number}>
                        <Col md={4}>
                            <Form.Control
                                value={i.title}
                                onChange={(e) => changeCoposition('name',e.target.value,i.number)}
                                placeholder="Введіть назву"/>
                        </Col>
                        <Col md={4}>
                            <Form.Control
                                value={i.amount}
                                onChange={(e) => changeCoposition('amount',e.target.value,i.number)}
                                placeholder="Введіть кількість" type = "number"/>
                        </Col>
                        <Col md={4}>
                           <Button
                               onClick={() => removeComposition(i.number)}
                               variant={"outline-danger"}>
                               Видалити
                           </Button>
                        </Col>
                    </Row>
                )}

                <Button
                    className="mt-3"
                    variant={"outline-dark"}
                    onClick={addComposition}
                >
                    Додати
                </Button>
            </Modal.Body>
            <Modal.Footer>
                <Button variant={"outline-danger"} onClick={onHide}>Закрити</Button>
                <Button variant={"outline-success"} onClick={addBox}>{isUpd?'Зберегти':'Додати '}</Button>
            </Modal.Footer>
        </Modal>
    );
};

export default CreateBox;
