import React, {useContext, useEffect, useState} from 'react';
import {Button, Container, Row} from "react-bootstrap";
import {DateRangePicker} from "rsuite";
import OrderList from "../components/OrderList";
import {Context} from "../index";
import {fetchOrders} from "../http/orderApi";

const ReportOrder = () => {

    const {order} = useContext(Context)

    const [show,setShow] = useState(false)

    const [dateRange,setDateRange] = useState([])
    const [filteredOrders,setFilteredOrders] = useState([])
    const [price,setPrice] = useState(0)
    const [selfPrice, setSelfPrice] = useState(0)
    const [boxAmount, setBoxAmount] = useState(0)
    const [postCardAmount, setPostCardAmount] = useState(0)
    const [box,setBox] = useState([])
    const [postCard,setPostCard] = useState([])




    const filterOrder = () => {
        console.log("filter:")
        let startDate = Date.parse(dateRange[0])/100000
        let endDate = Date.parse(dateRange[1])/100000
        order.orders.forEach( i =>{
            let checkDate = Date.parse(i.createdAt)/100000
            if(checkDate <= endDate){
                if (checkDate >= startDate)
                    order.setFilteredOrders([...order.filteredoOders,i])

            }
        })

    }

    const calcOrderAmount = () =>{
        let res = 0
        order.filteredoOders.forEach( order => res+=1)
        return res
    }

    const calcBoxAmount = () =>{
        let res = 0
        order.filteredoOders.forEach( order => res+= order.boxes.length)
        return res
    }
    const calcPostCardAmount = () =>{
        let res = 0
        order.filteredoOders.forEach( order => res+= order.post_cards.length)
        return res
    }
    const calcPrice = () =>{
        let res = 0
        order.filteredoOders.forEach( order => res+=order.price)
        return res
    }

    const calcSelfPrice = () =>{

    }

    const  showOrders =  () =>{
        order.setFilteredOrders([])
        filterOrder()
        console.log("filteredoOders")
        console.log(order.filteredoOders)
        console.log( 'Кількість замовлень: ' + calcOrderAmount())
        console.log( 'Зароблено : ' + calcPrice())
        console.log( 'Всього боксів : ' + calcBoxAmount())

        setShow(true)


    }

    useEffect(()=>{
        fetchOrders().then(data => {
            order.setOrders(data.rows)
        })

    },[])
    return (
       <Container>
           <Row>
               <div className="d-flex justify-content-center">
                   <div>
                       <DateRangePicker
                           showOneCalendar
                           size="lg"
                           appearance="subtle"
                           placeholder="Оберіть період"
                           style={{ width: 200 }}
                           value={dateRange}
                           onChange={(e) => {
                               setDateRange(e)
                           }}
                       />
                       <Button
                           onClick={()=> showOrders()}
                       >
                        Показати
                       </Button>

                   </div>
                   <div>
                       {show
                           ?filteredOrders.map(order =>
                               <div key={order.id}>{order.client_name}</div>
                           ):<div></div>}

                   </div>

               </div>
           </Row>
       </Container>
    );
};

export default ReportOrder;
