import React, {useState} from 'react';
import {Card, Col, Image, Button, Row } from "react-bootstrap";
import {useNavigate} from "react-router-dom";
import {BOXPAGE_ROUTE} from "../utils";


const PostCardItem = ({postCard}) => {
    const [isVisible,setIsVisible] = useState(false)
    const navigate = useNavigate()
    return (
        <Col md={3} className="m-2">
            <Card
                onClick={(e) => setIsVisible(!isVisible)}
                style={{width:150, cursor:"pointer"}}
                border={"light"}
            >
                <Image width={150} height={150} src={'http://localhost:5000/' + postCard.img}/>
                <div className="d-flex justify-content-between align-items-center">
                    <div>{postCard.name}</div>
                    <div>{postCard.price} </div>
                </div>
                {isVisible?
                    <Row className="d-flex justify-content-between mt-1">
                    <Button variant="outline-secondary" style={{width:80, fontSize:12}}>
                        Змінити
                    </Button>
                    <Button variant="outline-danger" style={{width:80, fontSize:12}}>
                        Видалити
                    </Button>
                </Row>:<Row>

                    </Row>
                }


            </Card>
        </Col>
    );
};

export default PostCardItem;
