import React, {useEffect, useState} from 'react';
import {Button, Card, Col, Container, Image, Row} from "react-bootstrap";
import {useNavigate, useParams} from 'react-router-dom'
import {deleteBox, fetchOneBox, updateBox} from "../http/boxApi";
import data from "bootstrap/js/src/dom/data";
import {MAIN_ROUTE} from "../utils";

const BoxPage = () => {
    const navigate = useNavigate()
    const [box,setBox] = useState({composition:[]})
    const {id} = useParams()


    const removeBox = () =>{
        try {
            deleteBox(id).then(data => navigate(MAIN_ROUTE) )
        }catch (e) {
            console.log('Помилка при видалені')}
    }

    const updBox = () => {
        try {
            updateBox(id).then(data => navigate(MAIN_ROUTE) )
        }catch (e) {
            console.log('Помилка при оновлені')}
    }
  //  const box = {id:1,name:"Перший бокс", price:2334, cbox:'Червона', img:'https://p2.zoon.ru/preview/U3a1uyz3s8zY1zpnQ3x9vg/2400x1500x85/1/7/5/original_5d5d700467cd9302f61f1858_5d5d70990a995.jpg'}
   /* const composition = [
        {id:1, name:'Milka', amount:3},
        {id:2, name:'Milka', amount:34},
        {id:3, name:'Milka', amount:3},
        {id:4, name:'Oreo', amount:3},
        {id:5, name:'Milka', amount:3},
        {id:6, name:'Sen', amount:3},
        {id:7, name:'Milka', amount:3},
        {id:8, name:'Milka', amount:3}
    ]*/
    useEffect(()=>{
        fetchOneBox(id).then(data=> {
            console.log("Useefff")
            console.log(data)
            setBox(data)
            console.log(box.composition)

        })
    },[])
        return (
        <Container>
            <Row className="mt-3">
                <Col md={4}>
                    <Image width={300} height={300} src={'http://localhost:5000/'+box.img}/>
                </Col>
                <Col md={4}>
                    <div className="mt-2" style={{fontSize:24}}>
                        {box.name}
                    </div>
                </Col>
                <Col md={4}>
                    <Card
                        className="d-flex flex-column align-items-center justify-content-around"
                        style={{fontSize:32, width:300, height:300, border:'5px solid lightgray'}}
                    >
                        <h3>{box.price} грн.</h3>
                        <div className="d-flex flex-column">
                            <Button
                                onClick={updBox}
                                variant={"outline-info"}
                                className="m-2">Змінити</Button>
                            <Button
                                onClick={removeBox}
                                variant={"outline-danger"}
                                className="m-2"
                            >
                                Видалити
                            </Button>
                        </div>

                    </Card>

                </Col>
            </Row>
            <Row className="d-flex flex-column m-3">
                <h2 className="m-2">Склад</h2>
                {box.composition.map((line, index) =>
                    <Row className="p-1" key={line.id} style={{background: index % 2 === 0? 'lightgray': 'transparent'}}>
                        {line.name} : {line.amount}
                    </Row>
                )}
            </Row>
        </Container>
    );
};

export default BoxPage;
